mapper.select:
select * from LGC_Book_ChangeRequest
where 1=1

	and BookId = #{bookId}


	and ReqestType = #{requestType}


	and Status = #{status}



	and CreateTime >= #{startDate,jdbcType=TIMESTAMP}




	and CreateTime <= #{endDate,jdbcType=TIMESTAMP}




 and DeleteFlag = '0'

 order by CreateTime desc


	 limit #{begin},#{limit}
