mapper.select:
 select * from LGC_Book
 where 1=1

 	and TicketId = #{ticketId}


 and  UserId = #{userId}


  and BookStatus = #{status}


		and CreatTime > #{startDate,jdbcType=TIMESTAMP}


		and CreateTime < #{endDate,jdbcType=TIMESTAMP}


		 limit #{pageStart},#{limit} 

  and DeleteFlag = '0'
  order by LastUpdateTime desc
