mapper.update:
update LGC_Ticket
set Deleted = 1
where ID = #{id,jdbcType=INTEGER}

update LGC_Ticket
set ExpireDate = #{expiredate,jdbcType=TIMESTAMP}
where ID = #{id,jdbcType=INTEGER} and Status = 0

update LGC_Ticket
set OwnerMobileNo = #{ownermobileno,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER} and Status = 0

update LGC_Ticket
set OwnerMobileNo = #{ownermobileno,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}
and OwnerMobileNo = #{premobileno,jdbcType=VARCHAR}
 and Status = 0

mapper.select:
select

from LGC_Ticket
where Deleted = 0

  and SerialNo = #{serialno,jdbcType=VARCHAR}


  and OwnerMobileNo = #{ownermobileno,jdbcType=VARCHAR}



  and CreateTime > #{beginDate,jdbcType=TIMESTAMP}




  and CreateTime < #{endDate,jdbcType=TIMESTAMP}



  and Status = #{status,jdbcType=INTEGER}

limit #{begin, jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

select count(1)
from LGC_Ticket
where Deleted = 0

  and SerialNo = #{serialno,jdbcType=VARCHAR}


  and OwnerMobileNo = #{ownermobileno,jdbcType=VARCHAR}



  and CreateTime > #{beginDate,jdbcType=TIMESTAMP}




  and CreateTime < #{endDate,jdbcType=TIMESTAMP}



  and Status = #{status,jdbcType=INTEGER}
